function JSMarkon(t){const e=t.split(/##/),n=/br/,r=/b\[(.*?)\]/,l=/link\[(.*?),(.*?)\]/,i=/list\[(.*?)\]/;return e.map(((t,e)=>{if(n.test(t))return"<br />";if(r.test(t)){let[,e]=t.match(r);return`<b>${e}</b>`}if(l.test(t)){let[,e,n]=t.match(l);return`<a href="${n}" style="color: blue; font-weight: bold;">${e}</a>`}if(i.test(t)){let[,e]=t.match(i);return`\n <ul>\n ${e.split(",").map(((t,e)=>`<li key=${e}>${t}</li>`)).join("")}\n </ul>\n `}return t})).join("")}